import React from "react";
import Navbar from "../Navbar/Navbar";

function Cart({ cartItems, handleAddProduct, handleRemoveProduct }) {

  const totalPrice = cartItems.reduce((price, item)=> price + item.quantity * item.price, 0)
  return (
    <div>
      <Navbar />
      {cartItems.length ===0}
      {cartItems.map((item) => {
        return (
          <>
            <div key={item.id} className="cart">
              <div className="card">
                <div>
                  <img src={item.image} width="180px" alt="" />
                </div>
                <div className="title">{item.title.substring(0, 11)}...</div>
                <div>Qty : {item.quantity}</div>
                <br />
                <div> Total Price : Rs. {item.price * item.quantity} /-</div>
                <button className="add" onClick={() => handleAddProduct(item)}>
                  +
                </button>
                <button
                  className="remove"
                  onClick={() => handleRemoveProduct(item)}
                >
                  -
                </button>
                <button>Buy Now</button>
              </div>
            </div>
          </>
        );
      })}
      <div className="total">Total price : Rs. {totalPrice} /- </div>
    </div>
  );
}

export default Cart;
