import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Navbar from "../Navbar/Navbar";

function Products() {
  const [data, setData] = useState([]);
  const [cartItems, setCartItems] = useState([])

  const handleAddProduct = (products)=>{
    alert("hi")
    const productExist = cartItems.find((item)=>item.id === product.id);
    if(productExist){
      setCartItems(cartItems.map((item)=> item.id === product.id ? 
      {...productExist, quantity : productExist.quantity + 1 }: item))
      console.log(cartItems)
    }
    else{
      setCartItems([...cartItems, {...product, quantity : 1}])
    }
  }


  useEffect(() => {
    axios
      .get("https://fakestoreapi.com/products")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <>
      <Navbar />
      <h1>High Range of Products</h1>
      {data.map((products) => {
        return (
          <>
            <div key={products.id} className="main">
              <div className="card">
                <div>
                  <img src={products.image} width="180px" alt="" />
                </div>
                <div className="title">
                  {products.title.substring(0, 11)}...
                </div>
                <div>Rs. {products.price} /-</div>
                <Link to="">
                  <button className="btn1" onClick={handleAddProduct}>
                    Add to Cart
                  </button>
                </Link>
                <Link to="/wishlist">
                  <button className="btn2">Add to Wishlist</button>
                </Link>
              </div>
            </div>
          </>
        );
      })}

      <h1>Cart Items</h1>

    </>
  );
}

export default Products;
