import React from "react";
import Navbar from "../Navbar/Navbar";

function Wishlist({ cartItems, handleAddProduct, handleRemoveProduct }) {

  return (
    <div>
      <Navbar cartItems={cartItems} />
      {cartItems.length === 0 && <h1>No Items are added</h1>}
      {cartItems.map((item) => {
        return (
          <>
            <div key={item.id} className="cart">
              <div className="card">
                <div>
                  <img src={item.image} width="170px" alt="" />
                </div>
                <div className="item_title">{item.title.substring(0, 11)}...</div>
                <div className="qty">Qty : {item.quantity}</div>
                <div> Total Price : Rs. {item.price * item.quantity} /-</div>
                <div>
                <button className="add" onClick={() => handleAddProduct(item)}>
                  +
                </button>
                <button
                  className="remove"
                  onClick={() => handleRemoveProduct(item)}
                >
                  -
                </button>
                </div>
              </div>
            </div>
          </>
        );
      })}
      <div className="final">
      <button className="btn">Buy Now</button>
      </div>
    </div>
  );
}

export default Wishlist;
