import React from 'react'

function Cart({cartItems}) {

  return (
    <div>Cart
      {cartItems.length===0 && <div>No Items are added in the cart</div>}
      <div>
        {cartItems.map((item)=>{
          <div key={item.id}>
             <img src={item.image} alt="" />
          </div>
        })}
      </div>
    </div>
  )
}

export default Cart
