import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { AddCart } from "../../Redux/action";
import Navbar from "../Navbar/Navbar";

function Products() {
  const [data, setData] = useState([]);
  const [cart, setCart] = useState([]);
  const dispatch = useDispatch;
  const addToCart = (products) => {
    dispatch(AddCart(products));
    set

  };

  useEffect(() => {
    axios
      .get("https://fakestoreapi.com/products")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <>
      <Navbar />
      <h1>High Range of Products</h1>
      {data.map((products) => {
        return (
          <>
            <div key={products.id} className="main">
              <div className="card">
                <div>
                  <img src={products.image} width="180px" alt="" />
                </div>
                <div className="title">
                  {products.title.substring(0, 11)}...
                </div>
                <div>Rs. {products.price} /-</div>
                <Link to="">
                  <button className="btn1" onClick={addToCart(products)}>
                    Add to Cart
                  </button>
                </Link>
                <Link to="/wishlist">
                  <button className="btn2">Add to Wishlist</button>
                </Link>
              </div>
            </div>
          </>
        );
      })}

      <h1>Cart Items</h1>


      {data.map((products) => {
        return (
          <>
            <div key={products.id} className="main">
              <div className="card">
                <div>
                  <img src={products.image} width="180px" alt="" />
                </div>
                <div className="title">
                  {products.title.substring(0, 11)}...
                </div>
                <div>Rs. {products.price} /-</div>
                <button>Buy Now</button>
              </div>
            </div>
          </>
        );
      })}



    </>
  );
}

export default Products;
