import React from 'react'

function Cart({cartItems}) {

  return (
    <div>Cart
      {cartItems.length===0 && <div>No Items are added in the cart</div>}
      <div>
      {data.map((products) => {
        return (
          <>
            <div key={products.id} className="main">
              <div className="card">
                <div>
                  <img src={products.image} width="180px" alt="" />
                </div>
                <div className="title">
                  {products.title.substring(0, 11)}...
                </div>
                <div>Rs. {products.price} /-</div>
                <Link to="">
                  <button className="btn1" onClick={handleProduct}>
                    Add to Cart
                  </button>
                </Link>
                <Link to="/wishlist">
                  <button className="btn2">Add to Wishlist</button>
                </Link>
              </div>
            </div>
          </>
        );
      })}
      </div>
    </div>
  )
}

export default Cart
