import React, { useState } from "react";
import { Link } from "react-router-dom";

function Register() {

  const initialValues = {
    name : "",
    email : "",
    password : ""
  }
  const [input, setInput] = useState(initialValues)

  const handleChange= ()=>{
    setInput()
  }


  const handleSubmit = (e)=>{
    e.preventDefault()
  }

  return (
    <div>
      <form action="/products" onSubmit={handleSubmit}>
        <h2>AJIO</h2>
        <div className="links">
          <Link to={"/"}>
            <h4>Register</h4>
          </Link>
          <div className="dash">|</div>
          <Link to={"/login"}>
            <h4>Login</h4>
          </Link>
        </div>
        <input type="text" value={input.name} placeholder="Username" required onChange={handleChange}/>
        <input type="email" value={input.email} placeholder="Email" required onChange={handleChange}/>
        <input type="password" value={input.password} placeholder="Password" required onChange={handleChange}/>
        <br />
        <button>Register</button>
      </form>
    </div>
  );
}

export default Register;
